import React from 'react';
import { StyleSheet, Text, View, ScrollView, ImageBackground, Dimensions, Button, props } from 'react-native';

export default () => (
            <ScrollView style={styles.container}>
                <ImageBackground style={styles.image} source={require('../images/image1.png')}>
                    {/* <Text style={styles.styletext} onPress={() => history.push("/shop")}>SHOP NOW</Text> */}
                    <Button title="SHOP NOW" style={styles.styletext} onPress={() => this.props.navigation.navigate('Shop')}></Button>
                </ImageBackground>
                <ImageBackground style={styles.image} source={require('../images/image2.png')}>
                    <Text style={styles.styletext} onPress={() => history.push("/wheretofind")}>WHERE TO FIND US</Text>
                </ImageBackground>
                <ImageBackground style={styles.image} source={require('../images/image3.png')}>
                    <Text style={styles.styletext} onPress={() => history.push("/chatnow")}>CHAT NOW</Text>
                </ImageBackground>
                <ImageBackground style={styles.image} source={require('../images/image4.png')}>
                    <Text style={styles.styletext} onPress={() => history.push("/contactus")}>CONTACT US</Text>
                </ImageBackground>
            </ScrollView>
        );

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    image: {
        width: Dimensions.get('window').width - 30,
        height: 105,
        marginTop: 20,
        flex: 1,
        borderRadius: 15,
        backgroundColor: '#fff',
        justifyContent: 'center',
        alignItems: 'center'
    },
    styletext: {
        color: '#fff',
        fontSize: 25,
        fontWeight: 'bold',
        
    }
});
