import React, { useState, useRef } from 'react';
import { View, Dimensions, Text} from 'react-native';
import { WebView } from 'react-native-webview';
import NavigationView from "./NavigationView";


const style = {
  container: {
      flex: 1,
      marginTop: 0,
      width: Dimensions.get('window').width,
      height: Dimensions.get('window').height,
      padding: 5,
      marginLeft: 5,
      marginRight: 5,
  },
};

// const HTML = '<h1>Durasafe</h1>';
// const HTML_SIZE_ADJUSMENT = `<!DOCTYPE html><html><head><meta name="viewport" content="width=device-width, initial-scale=1.0"></head><body> ${HTML} </body></html>`;

const ContactUs = () => {
const webViewRef = useRef();
const [canGoBack, setCanGoBack] = useState(false);
const [canGoForward, setCanGoForward] = useState(false);

const handleBackPress = () => {
  webViewRef.current.goBack();
}
const handleForwardPress = () => {
  webViewRef.current.goForward();
}

return(
  <View style={style.container}>
      <WebView 
      ref={webViewRef}
      source={{ uri: 'https://www.durasafe.com.sg/mobile-contact/' }} 
      originWhitelist={['*']}
      onNavigationStateChange={ state => {
      const back = state.canGoBack;
      const forward = state.canGoForward;
      setCanGoBack(back);
      setCanGoForward(forward);
      }}/>
      <NavigationView 
      onBackPress={handleBackPress} 
      onForwardPress={handleForwardPress}
      canGoBack={canGoBack}
      canGoForward={canGoForward}
      />

  </View>
)};

export default ContactUs;
